import initState from './reduce-abc';

export default function (state=initState, action){
  switch(action.type){
    case 'USER_SELECTED':
      return action.payload;
      break;
    case 'USER_REMOVED':
      if (action.index) {
        const newState = [...state];
        newState.splice(action.index, 1);
        return newState;
        } else {
          return state;
        }
      break;
  }
  return state;
}
