import initState from './reduce-abc';

export default function (state=initState, action){
  switch(action.type){
    case 'USER_SELECTED':
      return action.payload;
      break;
    case 'USER_REMOVED':
      const newState = [...state.slice(0, action.payload), ...state.slice(action.payload+1)];
      return newState;
      break;
  }
  return state;
}
