import Link from 'next/link';


const articles = [
  {
    "id":"1",
    "title":"<h2>1Duo Reges: constuctio interrete.</h2>",
    "content":"<p>is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industrys standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen</p>"
  },
  {
    "id":"2",
    "title":"<h2>2Duo Reges: constuctio interrete.</h2>",
    "content":"<p>is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industrys standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen</p>"
  },
  {
    "id":"3",
    "title":"<h2>3Duo Reges: constuctio interrete.</h2>",
    "content":"<p>is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industrys standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen</p>"
  },
  {
    "id":"4",
    "title":"<h2>4Duo Reges: constuctio interrete.</h2>",
    "content":"<p>is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industrys standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen</p>"
  }
];

function removeArticle(id)  {
  const articleList = Object.assign([], this.state.articleList);
  articleList.splice(id,1);
  this.setState({articleList:articleList})
  localStorage.clear();
  localStorage.setItem('articles', JSON.stringify(articleList));
};

// renderArticleList = () => {
//   return this.state.articleList.map((article,index) => <li><ArticleBrief article={article} removeArticle={()=>this.removeArticle(index)}/></li>);
// }

const Index = () => (
  <div>
    <div className="content">
        <div className="page-title">
            <h1 className="title">Articles</h1>
            <hr/>
          </div>
          <ul>
            <li>
                <div className="article-brief-container">
                
                  <Link href={{ pathname: '/article-page', query: { id: '1' } }} as='/article-page/1'>
                    <span dangerouslySetInnerHTML={{__html: articles[0].title}}>
                    </span>
                  </Link>
                  <p className="article-date"><small>20:41 | 3 Feb 2-19 | <a href="/#" ><span> Edit</span></a> | <button onClick={()=>removeArticle(index)}><span className="remove-btn"> Remove </span></button></small></p>
                </div>
            </li>
            <li>
              <div className="article-brief-container">
              <Link href={{ pathname: '/article-page', query: { id: '2' } }} as='/article-page/2'>
                <span dangerouslySetInnerHTML={{__html: articles[1].title}}>
                </span>
                </Link>
                <p className="article-date"><small>20:41 | 3 Feb 2-19 | <a href="/#" ><span> Edit</span></a> | <button ><span className="remove-btn"> Remove </span></button></small></p>
              </div>
            </li>
            <li>
              <div className="article-brief-container">
              <Link href={{ pathname: '/article-page', query: { id: '3' } }} as='/article-page/3'>              
                <span dangerouslySetInnerHTML={{__html: articles[2].title}}>
                </span>
                </Link>
                <p className="article-date"><small>20:41 | 3 Feb 2-19 | <a href="/#" ><span> Edit</span></a> | <button ><span className="remove-btn"> Remove </span></button></small></p>
              </div>
            </li>
            <li>
              <div className="article-brief-container">
              <Link href={{ pathname: '/article-page', query: { id: '4' } }} as='/article-page/4'>
                <span dangerouslySetInnerHTML={{__html: articles[3].title}}>
                </span>
                </Link>
                <p className="article-date"><small>20:41 | 3 Feb 2-19 | <a href="/#" ><span> Edit</span></a> | <button ><span className="remove-btn"> Remove </span></button></small></p>
              </div>
            </li>
          </ul>
        </div>
        <style jsx>{`

          .title {
            text-transform: uppercase;
            font-size: 2.2rem;
            text-align: center;

          }

          .page-title h2 {
            float: left;
          }
          .content {
            text-align: start;
            color: gray;
            margin-top: 50px;
            width: 80%;
            margin: auto;
            font-family: arial;
          }
          
          .article-title {
            margin: 0;
          }
          
          .article-date {
            margin: 0;
          }
          
          a {
            text-decoration: none;
          }
          
          .remove-btn {
            color: red;
          }
          `}
        </style>
  </div>
);
export default Index;
