const initState = [ {id: 1, name: 'a', surname: 'a'}, {id: 2, name: 'b', surname: 'b'}, {id: 3, name: 'c', surname: 'c'}, {id: 4, name: 'd', surname: 'd'}]; 

export default function (state=initState, action){
  switch(action.type){
    case 'USER_SELECTED':
      return action.payload;
      break;
    case 'USER_REMOVED':
    let newState = Object.keys(state).reduce((r, e) => {
      if(!action.payload[e]) r[e] = state[e];
      return r
    }, {})
    console.log(newState)
    return {state: newState}
      break;
  }
  return state;
}
