import React, { Component } from 'react';
// import '../style/article-page.css';
import Link from 'next/link';

const articles = [
  {
    "id":"1",
    "title":"<h2>1Duo Reges: constuctio interrete.</h2>",
    "content":"<p>is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industrys standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen</p>"
  },
  {
    "id":"2",
    "title":"<h2>2Duo Reges: constuctio interrete.</h2>",
    "content":"<p>is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industrys standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen</p>"
  },
  {
    "id":"3",
    "title":"<h2>3Duo Reges: constuctio interrete.</h2>",
    "content":"<p>is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industrys standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen</p>"
  },
  {
    "id":"4",
    "title":"<h2>4Duo Reges: constuctio interrete.</h2>",
    "content":"<p>is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industrys standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen</p>"
  }
];

class ArticlePage extends Component {
  static getInitialProps ({ query: { id } }) {
    return { postId: id }
}
  article ='';
  articleList='';
  constructor(props) {
    super(props);
    // this.articleList = JSON.parse(localStorage.getItem('articles'));
    this.article = this.articles.find(this.findArticle);
  }

  findArticle = () => { 
    return this.props.postId === this.props.match.params.id;
  }

  removeArticle = () => {
    const objIndex = this.articles.findIndex((obj => obj.id === this.props.match.params.id));
    this.articles.splice(objIndex,1);
    localStorage.clear();
    localStorage.setItem('articles', JSON.stringify(this.articles));
  };

  render() {
    
    return (
      <div className="article-content content">
      <div className="page-title">
      <Link href ="/"><h2>&lt; home</h2></Link>   
      {this.props.postId}         
            <h1 className="title">Articles</h1>
            <hr/>
          </div>
        <div dangerouslySetInnerHTML={{ __html: this.article.title }}></div>
          <p className="article-date"><small>20:41 | 3 Feb 2-19 | <a href="/#"><span> Edit </span></a> | <button onClick={this.removeArticle}><span className="remove-btn"> Remove </span></button></small></p>
          <hr/>
        <div dangerouslySetInnerHTML={{ __html: '<p>is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industrys standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen</p>' }}></div>
        <style jsx>{`
          .article-subtitle {
            margin: 0;
          }
          
          .article-content {
            text-align: start;
          }

          .page-title h2 {
            float: left;
          }

          .content {
            text-align: start;
            color: gray;
            margin-top: 50px;
            width: 80%;
            margin: auto;
            font-family: arial;
          }

          .title {
            text-transform: uppercase;
            font-size: 2.2rem;
            text-align: center;
          }
          a {
            text-decoration: none;
          }
          .remove-btn {
            color: red;
          }
    `}
    </style>
      </div>
    );
  }
}

