import initState from './reduce-abc';

export default function (state='', action){
  switch(action.type){
    case 'USER_SELECTED':
      return action.payload;
      break;
    case 'USER_REMOVED':
      const newState = [ ...state.slice(0, action.index),
        ...state.slice(action.index + 1) ];
      return newState;
      break;
  }
  return state;
}
