import initState from './reduce-abc';

export default function (state=initState, action){
  switch(action.type){
    case 'USER_SELECTED':
      return action.payload;
      break;
    case 'USER_REMOVED':
    const index = state.indexOf(action.payload);
    if (index === -1) {
      return state;
    }
    return state.slice(0, index).concat(state.slice(index + 1));
      break;
  }
  return state;
}
