import React, { Component } from 'react';
import {bindActionCreators} from 'redux';
import {connect} from 'react-redux';
import {selectUser} from './actions/index';

class AboutPage extends Component {
  constructor(props) {
    super(props);
    this.state = { articles: [ {name: 'a'}, {name: 'b'}, {name: 'c'}] };
  }

  render() {
    return (
      <div>
        {this.props.users.map((article,index) => <li onClick={() => this.props.selectUser(article)}>{article.name}</li>)}
      </div>
    );
  }
}

function mapStateToProps(state) {
    return {
      users: state.users
    }
}

function matchDispatchToProps(dispatch){
  return bindActionCreators({selectUser: selectUser}, dispatch)
}

export default connect(mapStateToProps, matchDispatchToProps)(AboutPage);
