import withRedux from "next-redux-wrapper";

const AboutPage = ({about}) => (
  <div>
    About {about}
  </div>
);

AboutPage.getInitialProps = ({store, isServer, pathname, query}) => {
  // component will read from store's state when rendered
  store.dispatch({type: 'FOO', payload: 'foo'});
  // pass some custom props to component from here
  return {custom: 'custom'}; 
};

AboutPage = withRedux(makeStore, (state) => ({foo: state.foo}))(AboutPage);

export default AboutPage;
